from datetime import datetimefrom croniter import croniterfrom loguru import loggerfrom lombok_plugin_repository.git_util import GitRepofrom lombok_plugin_repository.setting import ConfigObjectclass Action:    _cron: croniter = None    _next: datetime = None    @staticmethod    def init() -> None:        logger.info("初始化中...")        Action._cron = croniter(ConfigObject.config.cron)        Action._next = Action._cron.get_next()        for name, conf in ConfigObject.config.repos.items():            GitRepo.checkout_git_repo(name, conf)    @staticmethod    def single() -> None:        if Action._next is None or datetime.now() < Action._next:            return        Action._next = Action._cron.get_next()        logger.info("开始更新 Lombok Plugin")